import React, { useState } from "react";

import "./Calendar.css";

function GoalModal({ onClose, onSave }) {
  const [showCalendar, setShowCalendar] = useState(false);
  const [goal, setGoal] = useState("");

  const handleSave = () => {
    if (goal.trim()) {
      onSave(goal);
      setGoal("");
      onClose();
    }

    const handleChooseDay = () => {
      setShowCalendar(true);
    };
  };

  return (
    <div className="calendar-Modal">
      <div className="modal">
        <button className="modal-Close" onClick={onClose}>
          X
        </button>
        <h3 className="modal-Header">Add a Workout</h3>
        <input
          className="goal-Input"
          type="text"
          value={goal}
          onChange={(e) => setGoal(e.target.value)}
          placeholder="Enter Your Workout Goal"
        />
        <div className="calendar-set-rep-container">
          <input className="calendar-sets" type="number" placeholder="Sets" />
          <input className="calendar-reps" type="number" placeholder="Reps" />
        </div>
        <button className="calendar-choose-day">Choose Day</button>
        <button className="goal-Save" onClick={handleSave}>
          Save Workout
        </button>
        <button className="goal-Cancel" onClick={onClose}>
          Cancel
        </button>
      </div>
    </div>
  );
}

const Calendar = () => {
  const [goals, setGoals] = useState([]);
  const [showModal, setShowModal] = useState(false);
  return (
    <div className="calendar-container">
      <h2 className="calendar-title">Weekly Goals</h2>
      <button
        onClick={() => setShowModal(true)}
        className="calendar-goal-button"
      >
        Add Workout
      </button>
      {showModal && (
        <GoalModal
          onClose={() => setShowModal(false)}
          onSave={(goal) => setGoals([...goals, goal])}
        />
      )}
      <ul className="calendar-week">
        <li className="calendar-day">Monday</li>
        <li className="calendar-day">Tuesday</li>
        <li className="calendar-day">Wednesday</li>
        <li className="calendar-day">Thursday</li>
        <li className="calendar-day">Friday</li>
        <li className="calendar-day">Saturday</li>
        <li className="calendar-day">Sunday</li>
      </ul>
    </div>
  );
};

export default Calendar;
