import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./Workout.css";

const Workout = () => {
  const [workouts, setWorkouts] = useState([]);

  useEffect(() => {
    fetch("https://exercisedb.p.rapidapi.com/exercises/targetList", {
      method: "GET",
      headers: {
        "x-rapidapi-key": "ee1b2c0e2bmsh170e7f4f2a72bdap173617jsn425da2419ab4",
        "x-rapidapi-host": "exercisedb.p.rapidapi.com",
      },
    })
      .then((res) => res.json())
      .then((data) => {
        console.log("Fetched exercises:", data);
        setWorkouts(data); // this is already an array
      })
      .catch((err) => console.error("API error:", err));
  }, []);

  return (
    <div>
      <h1 className="workout-title">Workouts</h1>
      <div className="workout-card-container">
        <div className="workout-parts-list">
          <ul>
            {Array.isArray(workouts) &&
              workouts.map((part, index) => (
                <li className="workout-part" key={index}>
                  <button>{part}</button>
                </li>
              ))}
          </ul>
        </div>

        {workouts.map((workout) => (
          <div className="workout-card">
            <h2>{workout.name}</h2>
            <p>{workout.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Workout;
